import React, {useState, FormEvent} from 'react'
import PageHeader from '../../components/PageHeader';
import Input from '../../components/Input';

import warningIcon from '../../assest/images/icons/warning.svg'
import './styles.css';
import Textarea from '../../components/Textarea';
import Select from '../../components/Select';




function TeacherForm(){

    const [name, setName] = useState('');
    const [avatar, setAvatar] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [bio, setBio] = useState('');

    const [subject, setSubject ] = useState('');
    const [cost, setCost] = useState('');


    const [scheduleItems, setScheduleItems] = useState([
        {
            week_day:0,
            from:'',
            to:''
        }
    ]);


    function addNewScheduleItem(){
        setScheduleItems([ ...scheduleItems, { week_day:0,from:'', to:''}
                        ]);
    }

    // funcao para retonrar dia da semana 
    function setScheduleItemValue(position: number, field: string, value: string){
        const updateScheduleItems = scheduleItems.map((scheduleItem, index) =>{
            if (index === position){
                return {...scheduleItem, [field]: value };
            }
            return scheduleItem;

        });
        setScheduleItems(updateScheduleItems);
    }

    // funcao para conectar com o back
    function handleCreateClass(e: FormEvent){
        e.preventDefault();
        console.log({
            name, avatar,whatsapp,bio, subject,cost,scheduleItems
        });

    }


    return (
    <div id="page-teacher-form" className="container">
        <PageHeader 
            title="Que incríel que você quer dar aula."
            description = "O primeiro passo é preencher esse formuário de inscrição" 

        />

        <main>
            <form onSubmit={handleCreateClass}>
            <fieldset>
                <legend>Seus dados</legend>
                <Input  name="nome" 
                        label="Nome completo" 
                        value={name} 
                        onChange={(e) => {setName(e.target.value)}}
                />

                <Input 
                        name="avatar" 
                        label="Avatar"
                        value={avatar} 
                        onChange={(e) => {setAvatar(e.target.value)}}
                />


                <Input 
                        name="whatsapp" 
                        label="WhatsApp"
                        value={whatsapp} 
                        onChange={(e) => {setWhatsapp(e.target.value)}}
                />

                <Textarea 
                        name="bio" 
                        label="Biografia"
                        value={bio} 
                        onChange={(e) => {setBio(e.target.value)}}
                />
            </fieldset>

            <fieldset>
                <legend>Sobre a aula</legend>
                <Select 
                    name="subject" 
                    label="Matéria"
                    value={subject} 
                    onChange={(e) => {setSubject(e.target.value)}}
                    option = {[
                        {value:'Data Science', label:'Data Science'},
                        {value:'Machine Learning', label:'Machine Learning'},
                        {value:'Matemática', label:'Matemática'}
                    ]}

                />
                <Input  name="cost" 
                        label="Custo por hora aula"
                        value={cost} 
                        onChange={(e) => {setCost(e.target.value)}}
                />
            </fieldset>

            <fieldset>
                <legend>
                    Horários disponíveis  
                    <button type='button' onClick={addNewScheduleItem}>
                            + Novo Horário
                    </button>
                </legend>

                {scheduleItems.map((scheduleItem, index) =>{
                    return(
                        <div key={scheduleItem.week_day} className="schudele-item">
                <Select 
                    name="week_day" 
                    label="Dia da semana"
                    value = {scheduleItem.week_day}
                    onChange = {e => setScheduleItemValue(index, 'week_day', e.target.value)}
                    option = {[
                        {value:'0', label:'Domingo'},
                        {value:'1', label:'Segunda-feira'},
                        {value:'2', label:'Terça-feira'},
                        {value:'3', label:'Quarta-feira'},
                        {value:'4', label:'Quinta-feira'},
                        {value:'5', label:'Sexta-feira'},
                        {value:'6', label:'Sábado'}
                    ]}

                />
                <Input 
                    name="from" 
                    label="Das" 
                    type="time"
                    value = {scheduleItem.from}
                    onChange = {e => setScheduleItemValue(index, 'from', e.target.value)}
                />

                <Input 
                    name="to" 
                    label="Até"  
                    type="time"
                    value = {scheduleItem.to}
                    onChange = {e => setScheduleItemValue(index, 'to', e.target.value)}
                />

                </div>
                    );
                } )}

            </fieldset>

            <footer>
                <p>
                    <img src={warningIcon} alt="Aviso Importante"/>
                    Importante <br />
                    Preencha todos os dados
                </p>
                <button type="submit">
                    Salvar Cadastro
                </button>
            </footer>
            </form>
        </main>

    </div>

    )
}

export default TeacherForm;
