import React, {useState} from 'react'
import PageHeader from '../../components/PageHeader';
import Input from '../../components/Input';

import warningIcon from '../../assest/images/icons/warning.svg'
import './styles.css';
import Textarea from '../../components/Textarea';
import Select from '../../components/Select';




function TeacherForm(){
    const [scheduleItems, setScheduleItems] = useState([
        {
            week_day:0,
            from:'',
            to:''
        }
    ]);


    function addNewScheduleItem(){
        setScheduleItems([
            ...scheduleItems,
            {
                week_day:0,
                from:'',
                to:''
            }
        ]);
    }

    return (
    <div id="page-teacher-form" className="container">
        <PageHeader 
            title="Que incr칤el que voc칡 quer dar aula."
            description = "O primeiro passo 칠 preencher esse formu치rio de inscri칞칚o" 

        />

        <main>
            <fieldset>
                <legend>Seus dados</legend>
                <Input name="nome" label="Nome completo"/>
                <Input name="avatar" label="Avatar"/>
                <Input name="whatsapp" label="WhatsApp"/>
                <Textarea name="bio" label="Biografia"/>
            </fieldset>

            <fieldset>
                <legend>Sobre a aula</legend>
                <Select 
                    name="subject" 
                    label="Mat칠ria"
                    option = {[
                        {value:'Data Science', label:'Data Science'},
                        {value:'Machine Learning', label:'Machine Learning'},
                        {value:'Matem치tica', label:'Matem치tica'}
                    ]}

                />
                <Input name="cost" label="Custo por hora aula"/>
            </fieldset>

            <fieldset>
                <legend>
                    Hor치rios dispon칤veis  
                    <button type='button' onClick={addNewScheduleItem}>
                            + Novo Hor치rio
                    </button>
                </legend>

                {scheduleItems.map(scheduleItem =>{
                    return(
                        <div key={scheduleItem.week_day} className="schudele-item">
                <Select 
                    name="subject" 
                    label="Mat칠ria"
                    option = {[
                        {value:'Data Science', label:'Data Science'},
                        {value:'Machine Learning', label:'Machine Learning'},
                        {value:'Matem치tica', label:'Matem치tica'}
                    ]}

                />
                <Input name="from" label="Das" type="time"/>
                <Input name="to" label="At칠" type="time"/>

                </div>
                    );
                } )}

            </fieldset>

            <footer>
                <p>
                    <img src={warningIcon} alt="Aviso Importante"/>
                    Importante <br />
                    Preencha todos os dados
                </p>
                <button type="button">
                    Salvar Cadastro
                </button>
            </footer>
        </main>

    </div>

    )
}

export default TeacherForm;
